<style>
    .hero-deco-circle {
        position: absolute;
        pointer-events: none;
        z-index: 0;
    }
    .hero-deco-circle.top-0.right-0 {
        top: -40px;
        right: -40px;
    }
    .hero-deco-circle.bottom-0.left-0 {
        bottom: -40px;
        left: -40px;
    }

    .hero-deco-circle.top-0.right-0 circle,
    .hero-deco-circle.bottom-0.left-0 circle {
        opacity: 0.05;
    }

    /* Estilos para los iconos flotantes */
    .hero-deco-icon {
        position: absolute;
        pointer-events: none;
        z-index: 0;
        /* Estado inicial: invisible y animación pausada */
        opacity: 0;
        animation-play-state: paused;
        /* Transición para la propiedad 'opacity' */
        transition: opacity 0.7s ease-in-out;
    }

    /* Estado activo: visible y animación en ejecución */
    .animations-active .hero-deco-icon {
        opacity: 1;
        animation-play-state: running;
    }

    /* Iconos con animación de flotación simple */
    .node-icon {
        animation: float 10s ease-in-out infinite;
        width: 80px;
        height: 80px;
        top: 28%;
        left: 20%;
        animation-delay: -2s;
    }

    .svelte-icon {
        animation: drift 18s linear infinite;
        width: 100px;
        height: 100px;
        top: 28%;
        right: 20%;
        animation-delay: -5s;
    }

    /* Iconos con animación a la deriva */
    .nest-icon,
    .sql-icon {
        animation: drift 18s linear infinite;
    }

    .nest-icon {
        width: 70px;
        height: 70px;
        top: 22%;
        left: 35%;
        animation-duration: 22s; /* Ritmo más lento */
        animation-delay: -10s;
    }

    .sql-icon {
        width: 110px;
        height: 110px;
        top: 22%;
        right: 35%;
        animation-duration: 19s; /* Ritmo diferente */
        animation-delay: -3s;
    }

    /* Animación de flotación */
    @keyframes float {
        0% {
            transform: translate(0, 0) rotate(0deg);
        }
        50% {
            transform: translate(-15px, 15px) rotate(4deg);
        }
        100% {
            transform: translate(0, 0) rotate(0deg);
        }
    }

    /* Animación a la deriva */
    @keyframes drift {
        0% {
            transform: translate(0, 0) rotate(0deg);
        }
        20% {
            transform: translate(-10px, 15px) rotate(-6deg);
        }
        40% {
            transform: translate(8px, -10px) rotate(8deg);
        }
        60% {
            transform: translate(-15px, 8px) rotate(4deg);
        }
        80% {
            transform: translate(10px, -5px) rotate(-5deg);
        }
        100% {
            transform: translate(0, 0) rotate(0deg);
        }
    }
</style>